mapper.select:
SELECT

FROM HK_SYS_USER_INFO_T


        USER_ID = #{userId}


        AND USER_NAME = #{userName}


        AND USER_PASSWORD = #{userPassword}


        AND USER_TYPE = #{userType}


        AND PARENT_ID = #{parentId}


ORDER BY UPDATE_DATE_TIME DESC

    LIMIT #{pageNo}, #{pageSize}

